} else {    if(isset($HTTP_GET_VARS['msg'])){        $delete_msg = $HTTP_GET_VARS['msg'];        $msg_bits = explode(".",$delete_msg);        $back = $HTTP_GET_VARS['back'];    } else {        $valid = false;        $error_html = "<h2>No message specified for deleting</h2>";    }    if($msg_bits){        $ema = messages_get($msg_bits[0],$msg_bits[1],1);        if(count($ema) > 0){            $preview_message = $ema[0];            if($HTTP_COOKIE_VARS['bh_sess_uid'] != $preview_message['FROM_UID'] && !perm_is_moderator()){                edit_refuse();                exit;            }            $to_uid = $preview_message['TO_UID'];            $from_uid = $preview_message['FROM_UID'];        } else {            $valid = false;            $error_html = "<h2>Message " . $HTTP_GET_VARS['msg'] . " was not found</h2>";        }        unset($ema);    }}html_draw_top_script();/* echo "<table border=\"1\">";foreach ($HTTP_POST_VARS as $var => $value) {    echo "<tr><td>$var</td><td>$value</td></tr>";}echo "</table>"; */if($valid){    if(isset($HTTP_POST_VARS['submit'])){        echo $msg_bits[0].".".$msg_bits[1]."<br>";        $deleted = post_delete($msg_bits[0],$msg_bits[1]);        if($deleted){            echo "<p>&nbsp;</p>";            echo "<p>&nbsp;</p>";            echo "<div align=\"center\">";            echo "<p>Post deleted successfully</p>";            echo "<p><a href=\"discussion.php?msg=" . $HTTP_POST_VARS['t_back'];            echo "\" target=\"_self\">Return to messages</a></p>";            echo "</div>";            html_draw_bottom();            exit;        } else {            $error_html = "<h2>Error deleting post</h2>";        }    }    echo "<h2>Delete this message:</h2>";    echo "<h2>" . thread_get_title($msg_bits[0]) . "</h2>";    if($to_uid == 0){        $preview_message['TLOGON'] = "ALL";        $preview_message['TNICK'] = "ALL";    } else {        $preview_tuser = user_get($to_uid);        $preview_message['TLOGON'] = $preview_tuser['LOGON'];        $preview_message['TNICK'] = $preview_tuser['NICKNAME'];    }    $preview_tuser = user_get($from_uid);    $preview_message['FLOGON'] = $preview_tuser['LOGON'];    $preview_message['FNICK'] = $preview_tuser['NICKNAME'];    /*if($t_post_html != "Y"){        $preview_message['CONTENT'] = make_html($t_content);    } else {        $preview_message['CONTENT'] = $t_content;    }*/    message_display(0,$preview_message,0,0,false);}if(isset($error_html)){    echo $error_html;}echo "<p><form name=\"f_delete\" action=\"" . $HTTP_SERVER_VARS['PHP_SELF'] . "\" method=\"POST\" target=\"_self\">";echo form_input_hidden("t_msg",$delete_msg);echo form_input_hidden("t_back",$back);echo form_submit("submit","Delete");echo "&nbsp;".form_submit("cancel","Cancel");echo "</form>\n";echo "<p>&nbsp;&nbsp;</p>\n";html_draw_bottom();?>